mapper.select:

SELECT 
	COUNT(id)  
FROM map_review tb			
WHERE lat = #{lat} and lng = #{lng}
AND del_yn = "N"
AND regist_id = #{registId}


SELECT 
	id
FROM map_review tb				
WHERE lat = #{lat} and lng = #{lng}
AND del_yn = "N"
AND regist_id = #{registId}
ORDER BY regist_dt asc
limit #{startPage},#{endPage}

mapper.update:

UPDATE map_review
SET
	attach_file_master_id = #{attachFileMasterId}
WHERE
	id = #{id}
